import { Avatar, Text } from "@mantine/core";
import { IconHeartbeat, IconLayoutGrid } from "@tabler/icons-react";



const links=[
  {
    name:"Dashboard", url:"/dashboard", icon:<IconLayoutGrid stroke={1.5}/>
  }


]



const Sidebar = () => {
  return (
    // fixed width sidebar that doesn't shrink; add padding for appearance
    <div className='w-64 bg-red-300 flex flex-col gap-7 items-center py-3'>
      <div className="text-red-500 flex gap-1 items-center">
        <IconHeartbeat size={40} stroke={2.5} />
        <span className="font-heading font-semibold text-3xl">Pulse</span>

      </div>
      <div className="flex flex-col gap-1 items-center">
      <div className='p-1 bg-white rounded-full shadow-lg '>
       <Avatar className="rounded-full" radius="xl" size="xl" src="avatar.png" alt="Ram" />
       </div>
       <span className='font-medium '> Ramavatar</span>
       <Text c="dimmed" size="xs">Admin</Text>
      </div>


    </div>
  )
}

export default Sidebar;
