import { Avatar } from "@mantine/core";
import { IconHeartbeat } from "@tabler/icons-react";

const Sidebar = () => {
  return (
    // fixed width sidebar that doesn't shrink; add padding for appearance
    <div className='w-64 bg-red-300 flex flex-col gap-7 items-center py-3'>
      <div className="text-red-500 flex gap-1 items-center">
        <IconHeartbeat size={40} stroke={2.5} />
        <span className="font-heading font-semibold text-3xl">Pulse</span>

      </div>
      <div className='p-1 bg-white rounded-full shadow-xl '>
       <Avatar className="" radius="xl" size="xl" src="avatar.png" alt="Ram" />
      </div>


    </div>
  )
}

export default Sidebar;
